mapper.select:
SELECT * FROM t_board
WHERE category = #{category}
AND recruitNum  <  4 
AND isDel = 0
ORDER BY startDt
LIMIT #{sIdx}, #{rowCnt}

SELECT * FROM t_partyuser


		WHERE boardPk = #{boardPk}


		WHERE userPk = #{userPk}

mapper.update:
UPDATE t_board
SET recruitNum = recruitNum + 1
WHERE boardPk = #{boardPk}
