mapper.select:
 		SELECT * FROM t_user


		 		WHERE userId = #{userId} 			


		 		WHERE nickname = #{nickname} 			


		 		WHERE pNum = #{pNum} 			


 				WHERE userPk = #{userPk}

mapper.update:
UPDATE t_user
SET userPw = #{userPw}
WHERE userPk = #{userPk}

UPDATE t_user
SET userPoint = userPoint+ #{modPoint}
WHERE userId = #{userId}

UPDATE t_user
SET userPw = #{userPw}, nickname = #{nickname}, userMail = #{userMail}, pNum = #{pNum}, profileImg = #{profileImg}
WHERE userPk = #{userPk}
